import 'package:flutter/material.dart';import 'detail_store.dart';class DetailPage extends StatelessWidget {  const DetailPage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    DetailStore state = DetailStore();    return Scaffold(      appBar: AppBar(        centerTitle: true,        title: const Text("Detail Page"),        actions: [          TextButton(            onPressed: () {              state.createPost(context);            },            child: const Text(              "Edit",              style: TextStyle(                  color: Colors.white, fontSize: 19),            ),          ),        ],      ),      body: Stack(        children: [          Padding(            padding: const EdgeInsets.all(15.0),            child: Column(              children: [                TextField(                  controller: state.titleController,                  style: const TextStyle(                    fontSize: 16,                    fontWeight: FontWeight.w600,                    color: Colors.black,                  ),                  decoration:                  const InputDecoration(hintText: "Title"),                ),                const SizedBox(                  height: 15,                ),                TextField(                  controller: state.bodyController,                  style: const TextStyle(                    fontSize: 16,                    fontWeight: FontWeight.w600,                    color: Colors.black,                  ),                  decoration:                  const InputDecoration(hintText: "Body"),                ),              ],            ),          ),                 ],      ),    );  }}