import 'package:flutter/material.dart';import 'package:mobx/mobx.dart';import 'package:mobx_learning/models/post.dart';import 'package:mobx_learning/services/net_servic.dart';part 'detail_store.g.dart';class DetailStore = DetailStoreBase with _$DetailStore;abstract class DetailStoreBase with Store {  TextEditingController titleController = TextEditingController();  TextEditingController bodyController = TextEditingController();  @action  Future<void> createPost(BuildContext context) async {    String title = titleController.text.trim();    String body = bodyController.text.trim();    Post post = Post(id: "01", title: title, body: body, userId: 1);    await Network.POST(Network.API_CREATE, Network.paramsCreate(post));    Navigator.pop(context);  }}