import 'package:flutter/foundation.dart';import 'package:flutter_mobx/flutter_mobx.dart';import 'package:flutter/material.dart';import 'package:mobx_learning/pages/home/home_store.dart';import 'package:flutter_slidable/flutter_slidable.dart';class HomePage extends StatelessWidget {  const HomePage({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    HomeStore store = HomeStore()..getAllPost();    return Scaffold(      appBar: AppBar(),      body:  Observer(builder: (context){        if(store.isLoadig){          return Center(child: CircularProgressIndicator(),);        }        return ListView.builder(          itemCount: store.list.length,          itemBuilder: (context, index) {            return GestureDetector(              onTap: (){              },              child: Slidable(                startActionPane: ActionPane(                  extentRatio: 0.25,                  motion: const ScrollMotion(),                  children: [                    SlidableAction(                      onPressed: (context) {},                      backgroundColor: Colors.green,                      foregroundColor: Colors.white,                      icon: Icons.update,                    ),                  ],                ),                endActionPane: ActionPane(                  extentRatio: 0.25,                  motion: const ScrollMotion(),                  children: [                    SlidableAction(                      onPressed: (context) {                      },                      backgroundColor: Colors.red,                      foregroundColor: Colors.white,                      icon: Icons.delete_outline,                    ),                  ],                ),                child: Card(                  elevation: 5,                  child: ListTile(                    title: Text(                      store.list[index].title,                      style: const TextStyle(                        fontSize: 20,                        fontWeight: FontWeight.bold,                        color: Colors.black,                      ),                    ),                    subtitle: Text(                      store.list[index].body,                      style: const TextStyle(                        fontSize: 18,                        fontWeight: FontWeight.w600,                      ),                    ),                  ),                ),              ),            );          },        );      },),    floatingActionButton: FloatingActionButton(        child: const Icon(Icons.add),        onPressed: () => store.goToDetailPage(context),      ),    );  }}// import 'package:flutter/material.dart';// import 'package:flutter_mobx/flutter_mobx.dart';// import 'package:mobx_learning/models/post.dart';// import 'package:mobx_learning/pages/home/home_store.dart';//// class HomePage extends storelessWidget {//   const HomePage({Key? key}) : super(key: key);////   @override//   Widget build(BuildContext context) {//     HomeStore store = HomeStore()..getAllPost();//     return Scaffold(//       appBar: AppBar(//         title: const Text("All Posts"),//       ),//       body: Observer(//           builder: (context) {//             return ListView.builder(//               itemCount: store.list.length,//               itemBuilder: (context, index) {//                 Post post = store.list[index];////                 return Card(//                   child: ListTile(//                     title: Text(post.title),//                     subtitle: Text(post.body),//                   ),//                 );//               },//             );//           }//       ),////       floatingActionButton: FloatingActionButton(//         child: const Icon(Icons.add),//         onPressed: () => store.goToDetailPage(context),//       ),//     );//   }// }