import 'package:flutter/material.dart';import 'package:mobx/mobx.dart';import 'package:mobx_learning/models/post.dart';import 'package:mobx_learning/pages/detail/detail_home.dart';import 'package:mobx_learning/services/mapper.dart';import 'package:mobx_learning/services/net_servic.dart';part 'home_store.g.dart';class HomeStore = HomeStoreBase with _$HomeStore;abstract class HomeStoreBase with Store{  @observable  bool  isLoadig = false;  @observable  ObservableList<Post> list = ObservableList.of([]);  @action  Future<void> getAllPost()async{    isLoadig = true;    String? res = await Network.GET(Network.API_LIST, Network        .paramsEmpty());    print(res);    list = ObservableList.of(Mapper.parsePostList(res!));    isLoadig = false;  }  @action  Future<void> goToDetailPage(BuildContext context) async {    await Navigator.push(context, MaterialPageRoute(builder: (context) => const DetailPage()));    getAllPost();  }}